mapper.update:
update keyword
set search_flag = #{searchFlag,jdbcType=INTEGER},
  resource_flag = #{resourceFlag,jdbcType=INTEGER}
where keyword = #{keyword,jdbcType=CHAR}

mapper.select:
select keyword, search_flag, resource_flag
from keyword
where keyword = #{keyword,jdbcType=CHAR}

mapper.insert:
insert into keyword (keyword, search_flag, resource_flag)
values (#{keyword,jdbcType=CHAR}, #{searchFlag,jdbcType=INTEGER}, #{resourceFlag,jdbcType=INTEGER})
on duplicate key update search_flag=#{searchFlag,jdbcType=INTEGER}, resource_flag=#{resourceFlag,jdbcType=INTEGER}

mapper.update:
update keyword
 set resource_flag = #{resourceFlag,jdbcType=INTEGER}
where keyword = #{keyword,jdbcType=CHAR}
