mapper.update:
update keyword
set keyword_content = #{keywordContent,jdbcType=CHAR},
  moviemap_flag = #{moviemapFlag,jdbcType=INTEGER},
  resourcelist_flag = #{resourcelistFlag,jdbcType=INTEGER}
where keyword_id = #{keywordId,jdbcType=INTEGER}

mapper.select:
select keyword_id, keyword_content, moviemap_flag, resourcelist_flag
from keyword
where keyword_id = #{keywordId,jdbcType=INTEGER}
