mapper.insert:
insert into moviefrom (moviefrom_id, moviefrom_name, moviefrom_url
  )
values (#{moviefromId,jdbcType=INTEGER}, #{moviefromName,jdbcType=VARCHAR}, #{moviefromUrl,jdbcType=VARCHAR}
  )

mapper.update:
update moviefrom
set moviefrom_name = #{moviefromName,jdbcType=VARCHAR},
  moviefrom_url = #{moviefromUrl,jdbcType=VARCHAR}
where moviefrom_id = #{moviefromId,jdbcType=INTEGER}

mapper.select:
select moviefrom_id, moviefrom_name, moviefrom_url
from moviefrom
where moviefrom_id = #{moviefromId,jdbcType=INTEGER}
