mapper.delete:
delete from resource
where resource_url = #{resourceUrl,jdbcType=VARCHAR}
  and keyword = #{keyword,jdbcType=CHAR}

mapper.update:
update resource
set resource_name = #{resourceName,jdbcType=VARCHAR},
  resource_type = #{resourceType,jdbcType=INTEGER},
  client_type = #{clientType,jdbcType=INTEGER}
where resource_url = #{resourceUrl,jdbcType=VARCHAR}
  and keyword = #{keyword,jdbcType=CHAR}

mapper.select:
select resource_url, keyword, resource_name, resource_type, client_type
from resource
where resource_url = #{resourceUrl,jdbcType=VARCHAR}
  and keyword = #{keyword,jdbcType=CHAR}

select resource_url, keyword, resource_name, resource_type, client_type
from resource
  where keyword = #{keyword,jdbcType=CHAR}
