mapper.update:
update resourcelist_resourcetype
set resourcelist_id = #{resourcelistId,jdbcType=INTEGER},
  resourcetype_id = #{resourcetypeId,jdbcType=INTEGER}
where resourcelist_resourcetype_id = #{resourcelistResourcetypeId,jdbcType=INTEGER}

mapper.select:
select resourcelist_resourcetype_id, resourcelist_id, resourcetype_id
from resourcelist_resourcetype
where resourcelist_resourcetype_id = #{resourcelistResourcetypeId,jdbcType=INTEGER}
