mapper.delete:
delete from search
where movie_url = #{movieUrl,jdbcType=VARCHAR}
  and keyword = #{keyword,jdbcType=CHAR}

mapper.update:
update search
set movie_name = #{movieName,jdbcType=VARCHAR},
  client_type = #{clientType,jdbcType=INTEGER}
where movie_url = #{movieUrl,jdbcType=VARCHAR}
  and keyword = #{keyword,jdbcType=CHAR}

mapper.select:
select movie_url, keyword, movie_name, client_type
from search
where movie_url = #{movieUrl,jdbcType=VARCHAR}
  and keyword = #{keyword,jdbcType=CHAR}

select movie_url, keyword, movie_name, client_type
from search
where keyword=#{keyword,jdbcType=CHAR}
