mapper.delete:
delete from user_movie
where user_id = #{userId,jdbcType=INTEGER}
  and movie_id = #{movieId,jdbcType=INTEGER}

mapper.update:
update user_movie
set rate = #{rate,jdbcType=REAL},
  wish = #{wish,jdbcType=BIT},
  sean = #{sean,jdbcType=BIT}
where user_id = #{userId,jdbcType=INTEGER}
  and movie_id = #{movieId,jdbcType=INTEGER}

mapper.select:
select user_id, movie_id, rate, wish, sean
from user_movie
where user_id = #{userId,jdbcType=INTEGER}
  and movie_id = #{movieId,jdbcType=INTEGER}
